import React, {useState} from 'react';
import {Image, StyleSheet, View, TouchableOpacity} from 'react-native';
import {useSafeAreaInsets} from 'react-native-safe-area-context';

import {useNavigation} from '@react-navigation/native';
import {useTheme} from '@react-navigation/native';
import {SVGIcon} from '../components/SVGIcon';
import {UserPicker} from '../components/UserPicker';
import {SCText} from '../components/SCText';


export const ScreenHeader = ({title, showLogo = false, start}) => {
  const navigation = useNavigation();
  const insets = useSafeAreaInsets();

  const {colors} = useTheme();
  const [pickerVisible, setPickerVisible] = useState(false);

  return (
    <>
      <View
        style={[
          styles.container,
          {
            backgroundColor: colors.primary,
            height: 55 + insets.top,
            paddingTop: insets.top,
          },
        ]}>
          <TouchableOpacity
            onPress={() => {
              setPickerVisible(true);
            }}>
            {showLogo && (
              <Image
                source={{
                  uri:
                    'data:image/png;base64,',
                }}
                style={styles.logo}
              />
            )}
          </TouchableOpacity>
        <SCText
          style={[
            styles.title,
            {
              color: colors.textInverted,
            },
          ]}>
          {title}
        </SCText>
        <TouchableOpacity
          onPress={() => {
            navigation.navigate('MessageSearchScreen');
          }}
          style={styles.searchIconContainer}>
          <SVGIcon height="25" width="25" type="global-search" />
        </TouchableOpacity>
      </View>
      <UserPicker
        // ref={instance => (simplePicker.current = instance)}
        modalVisible={pickerVisible}
        onRequestClose={() => setPickerVisible(false)}
        label={'name'}
        onValueChange={userId => {
          setPickerVisible(false);
        }}
        value={'id'}
      />
    </>
  );
};

const styles = StyleSheet.create({
  container: {
    paddingLeft: 20,
    paddingRight: 20,
    justifyContent: 'space-between',
    alignItems: 'center',
    flexDirection: 'row',
  },
  logo: {
    height: 30,
    width: 30,
    borderRadius: 5,
  },
  title: {
    fontSize: 17,
    fontWeight: '600',
  },
  searchIconContainer: {
    // flex: 1,
    // height: '100%',
    // right: 20,
    // top: 15,
    // justifyContent: 'flex-end',
    // alignItems: 'flex-end',
  },
});
